##############################################################################
#
# Main
#
##############################################################################


# Check dependencies
type curl >/dev/null 2>&1 || { echo >&2 "ERROR: You do not have 'cURL' installed."; exit 1; }
type sed >/dev/null 2>&1 || { echo >&2 "ERROR: You do not have 'sed' installed."; exit 1; }
type column >/dev/null 2>&1 || { echo >&2 "ERROR: You do not have 'bsdmainutils' installed."; exit 1; }

#
# Process command line
#
# Pass all arguments before 'operation' to cURL except the ones we override
#
take_user=false
take_host=false
take_accept_header=false
take_contenttype_header=false

for key in "$@"; do
# Take the value of -u|--user argument
if [[ "$take_user" = true ]]; then
    basic_auth_credential="$key"
    take_user=false
    continue
fi
# Take the value of --host argument
if [[ "$take_host" = true ]]; then
    host="$key"
    take_host=false
    continue
fi
# Take the value of --accept argument
if [[ "$take_accept_header" = true ]]; then
    header_accept=$(lookup_mime_type "$key")
    take_accept_header=false
    continue
fi
# Take the value of --content-type argument
if [[ "$take_contenttype_header" = true ]]; then
    header_content_type=$(lookup_mime_type "$key")
    take_contenttype_header=false
    continue
fi
case $key in
    -h|--help)
    if [[ "x$operation" == "x" ]]; then
        print_help
        exit 0
    else
        eval "print_${operation}_help"
        exit 0
    fi
    ;;
    -V|--version)
    print_version
    exit 0
    ;;
    --about)
    print_about
    exit 0
    ;;
    -u|--user)
    take_user=true
    ;;
    --host)
    take_host=true
    ;;
    --force)
    force=true
    ;;
    -ac|--accept)
    take_accept_header=true
    ;;
    -ct|--content-type)
    take_contenttype_header=true
    ;;
    --dry-run)
    print_curl=true
    ;;
    -nc|--no-colors)
        RED=""
        GREEN=""
        YELLOW=""
        BLUE=""
        MAGENTA=""
        CYAN=""
        WHITE=""
        BOLD=""
        OFF=""
        result_color_table=( "" "" "" "" "" "" "" )
    ;;
    checkAdminAccess)
    operation="checkAdminAccess"
    ;;
    checkGeneralAccess)
    operation="checkGeneralAccess"
    ;;
    checkReadAccess)
    operation="checkReadAccess"
    ;;
    checkWriteAccess)
    operation="checkWriteAccess"
    ;;
    convertFromCollectionFormatToRoCrate)
    operation="convertFromCollectionFormatToRoCrate"
    ;;
    convertFromRoCrateToCollectionFormat)
    operation="convertFromRoCrateToCollectionFormat"
    ;;
    exportParsed)
    operation="exportParsed"
    ;;
    exportUnparsed)
    operation="exportUnparsed"
    ;;
    importParsed)
    operation="importParsed"
    ;;
    importUnparsed)
    operation="importUnparsed"
    ;;
    root)
    operation="root"
    ;;
    getDatasetSchema)
    operation="getDatasetSchema"
    ;;
    validateMetadata)
    operation="validateMetadata"
    ;;
    mintDataset)
    operation="mintDataset"
    ;;
    updateMetadata)
    operation="updateMetadata"
    ;;
    generateReadAccessCredentials)
    operation="generateReadAccessCredentials"
    ;;
    generateWriteAccessCredentials)
    operation="generateWriteAccessCredentials"
    ;;
    fetchDataset)
    operation="fetchDataset"
    ;;
    listAllDatasets)
    operation="listAllDatasets"
    ;;
    *==*)
    # Parse body arguments and convert them into top level
    # JSON properties passed in the body content as strings
    if [[ "$operation" ]]; then
        IFS='==' read -r body_key sep body_value <<< "$key"
        body_parameters[${body_key}]="\"${body_value}\""
    fi
    ;;
    *:=*)
    # Parse body arguments and convert them into top level
    # JSON properties passed in the body content without quotes
    if [[ "$operation" ]]; then
        # ignore error about 'sep' being unused
        # shellcheck disable=SC2034
        IFS=':=' read -r body_key sep body_value <<< "$key"
        body_parameters[${body_key}]=${body_value}
    fi
    ;;
    +([^=]):*)
    # Parse header arguments and convert them into curl
    # only after the operation argument
    if [[ "$operation" ]]; then
        IFS=':' read -r header_name header_value <<< "$key"
        header_arguments[$header_name]=$header_value
    else
        curl_arguments+=" $key"
    fi
    ;;
    -)
    body_content_temp_file=$(mktemp)
    cat - > "$body_content_temp_file"
    ;;
    *=*)
    # Parse operation arguments and convert them into curl
    # only after the operation argument
    if [[ "$operation" ]]; then
        IFS='=' read -r parameter_name parameter_value <<< "$key"
        if [[ -z "${operation_parameters[$parameter_name]+foo}" ]]; then
            operation_parameters[$parameter_name]=$(url_escape "${parameter_value}")
        else
            operation_parameters[$parameter_name]+=":::"$(url_escape "${parameter_value}")
        fi
    else
        curl_arguments+=" $key"
    fi
    ;;
    *)
    # If we are before the operation, treat the arguments as cURL arguments
    if [[ "x$operation" == "x" ]]; then
        # Maintain quotes around cURL arguments if necessary
        space_regexp="[[:space:]]"
        if [[ $key =~ $space_regexp ]]; then
            curl_arguments+=" \"$key\""
        else
            curl_arguments+=" $key"
        fi
    fi
    ;;
esac
done


# Check if user provided host name
if [[ -z "$host" ]]; then
    ERROR_MSG="ERROR: No hostname provided!!! Define env variable DATA_API_HOST like 'export DATA_API_HOST=...' or provide on command line option '--host ...'"
    exit 1
fi

# Check if user specified operation ID
if [[ -z "$operation" ]]; then
    ERROR_MSG="ERROR: No operation specified!!!"
    exit 1
fi


# Run cURL command based on the operation ID
case $operation in
    checkAdminAccess)
    call_checkAdminAccess
    ;;
    checkGeneralAccess)
    call_checkGeneralAccess
    ;;
    checkReadAccess)
    call_checkReadAccess
    ;;
    checkWriteAccess)
    call_checkWriteAccess
    ;;
    convertFromCollectionFormatToRoCrate)
    call_convertFromCollectionFormatToRoCrate
    ;;
    convertFromRoCrateToCollectionFormat)
    call_convertFromRoCrateToCollectionFormat
    ;;
    exportParsed)
    call_exportParsed
    ;;
    exportUnparsed)
    call_exportUnparsed
    ;;
    importParsed)
    call_importParsed
    ;;
    importUnparsed)
    call_importUnparsed
    ;;
    root)
    call_root
    ;;
    getDatasetSchema)
    call_getDatasetSchema
    ;;
    validateMetadata)
    call_validateMetadata
    ;;
    mintDataset)
    call_mintDataset
    ;;
    updateMetadata)
    call_updateMetadata
    ;;
    generateReadAccessCredentials)
    call_generateReadAccessCredentials
    ;;
    generateWriteAccessCredentials)
    call_generateWriteAccessCredentials
    ;;
    fetchDataset)
    call_fetchDataset
    ;;
    listAllDatasets)
    call_listAllDatasets
    ;;
    *)
    ERROR_MSG="ERROR: Unknown operation: $operation"
    exit 1
esac
